        <script>
            document.addEventListener('keydown', function(event) {
                let isHover = false;
                const cursor = event.cursor;
                if (isHover && (event.target?.occupied || event.target?.scroll) !== cursor) {
                    isHover = true;
                }

                switch(event.key) {
                    case 'ArrowLeft':
                        if (isHover && !event.target.classList.contains('hover')) {
                            isHover = true;
                            event.target.classList.add('hover');
                        }
                        break;

                    case 'ArrowRight':
                        if (isHover && !event.target.classList.contains('hover')) {
                            isHover = false;
                            event.target.classList.remove('hover');
                        }
                        break;

                    case 'ArrowDown':
                        if (isHover && event.target.classList.contains('hover')) {
                            isHover = false;
                        }
                        break;

                    case 'ArrowUp':
                        if (isHover && !event.target.classList.contains('hover')) {
                            isHover = true;
                        }
                        break;

                    case 'Enter':
                        if (isHover && !event.target.classList.contains('hover')) {
                            isHover = true;
                        }
                        break;
                    default:
                        if (event.target?.scroll && !event.target.classList.contains('hover')) {
                            event.target.classList.add('hover');
                        }
                        if (event.target?.occupied && !isHover) {
                            isHover = true;
                        }
                        event.target.classList.add('cursor', cursor);
                    break;
                }
            });
        </script>
    </div>
</body>
</html>
