<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Modern Restaurant Chain</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <link href="https://fonts.googleapis.com/css2?family=Poppins:wght@300;400;500;600&display=swap" rel="stylesheet">
    <style>
        .container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
        }

        .header {
            background-color: #1a2f38;
            border-bottom: 2px solid #0456b3;
            padding-bottom: 1rem;
            color: white;
        }

        .nav {
            background-color: #2b3f56;
        }

        .nav ul {
            list-style: none;
            display: flex;
            justify-content: center;
            gap: 2rem;
        }

        .nav li {
            margin-left: 2rem;
            padding: 0.5rem 1rem;
        }

        .nav li:hover {
            background-color: #0456b3 !important;
            border: 1px solid #0456b3 !important;
        }

        .content {
            flex: 1;
        }

        .social-icons {
            display: grid;
            grid-template-columns: repeat(2, 1fr);
            gap: 1rem;
        }

        .social-icons span {
            font-size: 0.875rem;
        }

        .newsletter-form {
            max-width: 600px;
            margin: 0 auto;
        }

        input, textarea {
            width: 100%;
            padding: 0.5rem;
            margin-bottom: 1rem;
        }

        .contact-info {
            background-color: #2b3f56;
            padding: 1rem;
            text-align: center;
        }

        .contact-info h3 {
            margin: 0.5rem 0;
            color: #0456b3;
        }

        .contact-info p {
            margin: 0.5rem 0;
        }

        @media (max-width: 768px) {
            .container {
                padding: 1rem;
            }

            .header h1 {
                font-size: 1.5rem;
            }
        }
    </style>
</head>
<body class="bg-gray-50">
    <div class="container">
        <!-- Header Section -->
        <header class="header fixed-top bg-gradient-to-b from-gray-900 to-white text-white">
            <h1 class="text-3xl font-bold mb-4">Modern Dishes</h1>
            <p class="text-xl text-gray-700 mb-8"> transcend the basics with a truly innovative menu experience</p>
            <p class="text-gray-400">Image id=48 source: picsum.photos</p>
        </header>

        <!-- Navigation -->
        <nav class="nav fixed-top bg-white">
            <div class="container mx-auto flex items-center justify-between">
                <div class="nav-item">
                    <span class="text-gray-600 mr-2">Location</span>
                </div>
                <div class="nav-item">
                    <span class="text-gray-600 mr-2">Menu</span>
                </div>
                <div class="nav-item">
                    <span class="text-gray-600 mr-2">Order</span>
                </div>
                <div class="nav-item">
                    <a href="#" class="text-blue-500 hover:text-blue-400">Contact</a>
                </div>
            </div>
        </nav>

        <!-- Content Sections -->
        <section class="container mx-auto py-6">
            <div class="row">
                <div class="col-md-4">
                    <h2 class="mb-4">Dining Experience</h2>
                    <p class="text-gray-600">Savor the finest cuisines in every bite</p>
                    <div class="content">
                        <span class="text-blue-500">Menu items are ready to be served in 30 minutes or less</span>
                        <h3 class="mb-2">Our Top Dishes</h3>
                        <ul class="list-disc list-inside text-gray-700">
                            <li class="text-gray-700">Classic Italian Dish</li>
                            <li class="text-gray-700">Mexican Tacos</li>
                            <li class="text-gray-700">Chinese Delight</li>
                            <li class="text-gray-700">French Brio</li>
                        </ul>
                    </div>
                </div>
            </div>
        </section>

        <section class="container mx-auto py-6 mt-24">
            <div class="row">
                <div class="col-md-6">
                    <h2 class="mb-4">About Us</h2>
                    <p class="text-gray-600">Join us in our quest for discovery</p>
                    <div class="content">
                        <h3 class="text-gray-400 bold">Social Media</h3>
                        <div class="social-icons">
                            <img src="https://picsum.photos/12/3/12116.png?80" alt="Facebook">
                            <img src="https://picsum.photos/9/3/12116.png?80" alt="Twitter">
                            <img src="https://picsum.photos/10/3/12116.png?80" alt="Instagram">
                        </div>
                    </div>
                </div>
            </div>
        </section>

        <!-- Footer -->
        <footer class="content fixed-bottom bg-white border-t">
            <div class="row">
                <div class="col-md-4">
                    <div class="social-icons">
                        <img src="https://picsum.photos/2/3/12116.png?80" alt="Facebook">
                        <img src="https://picsum.photos/10/3/12116.png?80" alt="Twitter">
                        <img src="https://picsum.photos/5/3/12116.png?80" alt="Instagram">
                    </div>
                </div>
                <div class="col-md-4">
                    <h3 class="text-gray-400 mb-2">Contact Information</h3>
                    <p class="text-gray-600">123 Restaurant Street<br>
                        City, State<br>
                        Phone: (555) 123-4567</p>
                </div>
            </div>
        </footer>

        <!-- Footer's Newsletter Form -->
        <div class="newsletter-form">
            <h3 class="text-center mb-4">Stay Updated</h3>
            <p class="text-gray-600">Subscribe to our newsletter for special offers and exclusive content</p>
            <form>
                <input type="email" placeholder="Enter your email" required>
                <input type="text" placeholder="First name" required>
                <input type="text" placeholder="Last name" required>
                <button type="submit">Send Message</button>
            </form>
        </div>
    </div>

    <script>
        // Font styles
        document.addEventListener('DOMContentLoaded', function() {
            window.onload = function() {
                document.querySelectorAll('.text').forEach(element => {
                    element.addEventListener('DOMContentLoaded', function() {
                        return this.style.color = '#102F41';
                    });
                });
            });
        });
    </script>
</body>
</html>
